<template>    <nb-container>        <view class="container">            <AutoHeightImage                    :source="require('../assets/images/bg.jpg')"                    :width="screen.width"                    :style="{flex: 1, resizeMode: 'stretch'}"            ></AutoHeightImage>            <view v-if="isSpinner" class="overlay" :style="{ backgroundColor: 'rgba(0,0,0,0.4)', height: screen.height, zIndex: 2}">                <nb-spinner color="red" :style="{top: '45%'}"/>            </view>            <view class="overlay">                <nb-content padder>                    <view                            :style="{                                width: viewScreen(80,'vw'),                                height: viewScreen(70,'vh'),                                marginTop: viewScreen(15,'vh'),                                marginBottom: viewScreen(15,'vh'),                                marginLeft: viewScreen(6,'vw'),                            }"                    >                        <view                                :style="{                                        borderRadius: viewScreen(12.5, 'vw', 15),                                        width: viewScreen(25,'vw', 30),                                        paddingTop: 15,                                        paddingBottom: 15,                                        alignItems: 'center',                                        backgroundColor: 'white'                                    }"                        >                            <AutoHeightImage                                    :source="require('../assets/images/logo.png')"                                    :width="screen.width/5"                            ></AutoHeightImage>                        </view>                        <nb-text class="title mt-10percent"                                 :style="{fontSize: scaleFontSize(20),textShadowColor: 'rgba(0,0,0,0.5)',textShadowOffset: {width: -4, height: -1.5},textShadowRadius: 1}">KÍNH CHÀO QUÝ KHÁCH                        </nb-text>                        <nb-text class="description" :style="{fontSize: scaleFontSize(12)}">Đăng nhập để tiếp tục                        </nb-text>                        <nb-form class="mt-10percent">                            <nb-item class="item-input-border ml-0"                                     :style="{                                        marginBottom: isErrorUsername ? 0 : viewScreen(3,'vh'),                                        borderColor: isErrorUsername ? '#ed2f2f' : 'rgba(255,255,255,0.1)',                                        borderBottomWidth: 2,                                        borderTopWidth: 2,                                        borderLeftWidth: 2,                                        borderRightWidth: 2,                                    }">                                <nb-label                                        :style="{                                            position: 'absolute',                                            top: isUsernameLabel ? '15%' : '35%',                                            left: '5%',                                            color: isUsernameLabel ? 'rgba(255,255,255, 0.5)' : 'white',                                            fontSize: isUsernameLabel ? scaleFontSize(8) : scaleFontSize(14),                                          }"                                >Số điện thoại                                </nb-label>                                <nb-input                                        :style="{                                            position: 'absolute',                                            top: 0,                                            left: '5%',                                            color: 'white',                                            paddingLeft: 0,                                            paddingRight: 0,                                            marginTop: 2,                                            marginLeft: 0,                                            fontSize: scaleFontSize(14),                                            height: '100%',                                            width: '80%'                                          }"                                        :on-focus="handleFocusUserName" v-model="username"                                        :on-change-text="handleChangeValueUserName"/>                                <nb-icon v-if="isErrorUsername" name="close-circle" :style="{marginLeft: '88%', color: '#ed2f2f'}" />                            </nb-item>                            <nb-item v-if="isErrorUsername" class="ml-0 no-border" :style="{marginBottom: viewScreen(3,'vh')}">                                <nb-text class="text-danger" :style="{fontStyle: 'italic', fontSize: scaleFontSize(12)}">{{isErrorTextUsername}}</nb-text>                            </nb-item>                            <nb-item class="item-input-border ml-0"                                     :style="{                                           borderColor: isErrorPassword ? '#ed2f2f' : 'rgba(255,255,255,0.1)',                                           borderBottomWidth: 2,                                           borderTopWidth: 2,                                           borderLeftWidth: 2,                                           borderRightWidth: 2,                                     }">                                <nb-label                                        :style="{                                            position: 'absolute',                                            top: isPasswordLabel ? '15%' : '35%',                                            left: '5%',                                            color: isPasswordLabel ? 'rgba(255,255,255,0.5)' : 'white',                                            fontSize: isPasswordLabel ? scaleFontSize(8) : scaleFontSize(14),                                            }"                                >Mật khẩu                                </nb-label>                                <nb-input                                        :style="{                                            position: 'absolute',                                            top: 0,                                            left: '5%',                                            color: 'white',                                            paddingLeft: 0,                                            paddingRight: 0,                                            marginTop: 2,                                            marginLeft: 0,                                            fontSize: scaleFontSize(14),                                            height: '100%',                                            width: '80%'                                     }"                                        :on-focus="handleFocusPassword" auto-capitalize="none"                                        secure-text-entry v-model="password"                                        :on-change-text="handleChangeValuePassword"/>                                <nb-icon v-if="isErrorPassword" name="close-circle" :style="{marginLeft: '88%', color: '#ed2f2f'}" />                            </nb-item>                            <nb-item v-if="isErrorPassword" class="ml-0 no-border"                                     :style="{                                            marginBottom: viewScreen(3, 'vh')                                     }"                            >                                <nb-text class="text-danger" :style="{fontStyle: 'italic', fontSize: scaleFontSize(12)}">{{isErrorTextPassword}}</nb-text>                            </nb-item>                            <nb-item class="ml-0 no-border" :style="{marginTop: viewScreen(3, 'vh'), height: viewScreen(8, 'vh'), width: '100%'}">                                <nb-button iconRight light :on-press="handleLogin"                                           :style="{ height: '100%',width: '100%', position: 'relative'}">                                    <nb-text class="text-login"                                             :style="{                                             position: 'absolute',                                             top: viewScreen(5, 'vh') - scaleFontSize(16),                                            fontSize: scaleFontSize(16),                                            textShadowColor: 'rgba(48,63,159, 0.35)',                                            textShadowOffset: {width: 1, height: 1},                                            textShadowRadius: 5,                                        }">Đăng Nhập                                    </nb-text>                                    <nb-icon class="text-login" active name="arrow-forward"                                             :style="{                                                position: 'absolute',                                                left: '90%',                                               // marginLeft: viewScreen(2, 'vw'),                                                fontSize: scaleFontSize(17),                                                textShadowColor: 'rgba(48,63,159, 0.35)',                                                textShadowOffset: {width: 1, height: 1},                                                textShadowRadius: 5,                                             }"                                    />                                </nb-button>                            </nb-item>                            <nb-item class="ml-0" :style="{height: viewScreen(8, 'vh'), borderColor: 'transparent', justifyContent:'space-between'}">                                <nb-text class="text-light" :style="{fontSize: scaleFontSize(14)}" :on-press="handleForgotPassword">Quên mật khẩu?                                </nb-text>                                <nb-text class="text-light" :style="{fontSize: scaleFontSize(14), marginLeft: viewScreen(-6, 'vw')}" :on-press="handleSignUp">Đăng kí tài khoản                                </nb-text>                            </nb-item>                        </nb-form>                    </view>                </nb-content>            </view>        </view>    </nb-container></template><script>    import React, {Component} from 'react';    import {Dimensions, Animated, Easing} from 'react-native'    import AutoHeightImage from 'react-native-auto-height-image';    import {Constants, MapView, Permissions, Location} from "expo";    import LibCustom from '../library/custom';    export default {        props: {            navigation: {                type: Object,            }        },        components: {            AutoHeightImage,        },        computed: {},        data: function () {            return {                screen: {                    width: 0,                    height: 0,                },                isUsernameLabel: false,                isPasswordLabel: false,                isErrorUsername: false,                isErrorTextUsername: '',                isErrorPassword: false,                isErrorTextPassword: '',                isSpinner: false,                username: '',                password: '',            };        },        created: function () {        },        mounted: function () {            this.screen = LibCustom.getSizeScreen();        },        methods: {            handleFocusUserName: function () {                this.isUsernameLabel = true;                this.isPasswordLabel = false;                if (this.password) {                    this.isPasswordLabel = true;                }            },            handleFocusPassword: function () {                this.isUsernameLabel = false;                this.isPasswordLabel = true;                this.isErrorPassword = false;                if (this.username) {                    this.isUsernameLabel = true;                }else{                    this.isErrorUsername = true;                    this.isErrorTextUsername = 'Bạn vui lòng nhập vào số điện thoại';                }            },            handleChangeValueUserName: function () {                if (this.username === "") {                    this.isUsernameLabel = false;                    this.isErrorUsername = false;                } else {                    if(!this.username.match(/^[0-9]{1,}$/)){                        this.isErrorUsername = true;                        this.isErrorTextUsername = 'Số điện thoại chỉ chứa số';                    }else{                        this.isErrorUsername = false;                    }                    this.isUsernameLabel = true;                }            },            handleChangeValuePassword: function () {                if (this.password === "") {                    this.isPasswordLabel = false;                    this.isErrorPassword = false;                } else {                    this.isPasswordLabel = true;                }            },            scaleFontSize: function (size) {                return LibCustom.scaleFontSize(size);            },            viewScreen: function (percent, type, minus = 0) {                return LibCustom.viewScreen(percent, type, minus);            },            handleLogin: function(){                if(this.username === "" || this.password === "" || this.isErrorUsername || this.isErrorPassword ){                    if(this.username === ""){                        this.isErrorUsername = true;                        this.isErrorTextUsername = 'Bạn vui lòng nhập vào số điện thoại';                    }                    if(this.password === ""){                        this.isErrorPassword = true;                        this.isErrorTextPassword = "Bạn vui lòng nhập vào mật khẩu";                    }                }else{                    this.isSpinner = true;                }            },            handleForgotPassword: function(){                alert("ban quen mat khau");            },            handleSignUp: function(){                alert("ban da click vao dang ki tai khoan");            }        },    };</script><style>    .container {        flex: 1;        justify-content: center;        align-items: center;        background-color: transparent;        position: relative;    }    .overlay {        position: absolute;        top: 0;        left: 0;        right: 0;        bottom: 0;    }    .title {        color: white;        font-weight: 900;    }    .description {        color: rgba(255, 255, 255, 0.4);    }    .text-login {        color: #303F9F;        font-weight: bold;    }    .text-light {        color: white;    }    .item-input-border {        border-color: rgba(255, 255, 255, 0.1);        background-color: rgba(255, 255, 255, 0.1);        border-radius: 5;        position: relative;        margin: 0;        height: 20%;    }    .ml-0 {        margin-left: 0;    }    .mt-5percent {        margin-top: 5%;    }    .mt-10percent {        margin-top: 10%;    }    .pr-10 {        padding-right: 10;    }    .no-border{        border-color: transparent;    }    .text-danger{        color: #ed2f2f;    }</style>